mapper.select:

select * from (
	select rownum as rnum, c.* from 
		(select  /*+ index_desc(comments comment_pk_cno) */ cno, bno, writer, writetime, profile, content from comments where bno=#{bno}) c  
	where rownum<=#{rownum.endRowNum}
) 
where rnum>=#{rownum.startRowNum}


